import React from "react";
import "./forecast.scss";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import { CardHeader } from "@mui/material";
import Stack from "@mui/material/Stack";
import Paper from "@mui/material/Paper";
import { Clock } from "./clock";

const Forecast = (props) => {
  const converter = (cel) => {
    let temp = parseFloat(cel - 273.15).toFixed(1);
    return temp;
  };

  return (
    <div className="main">
      {props.we.cod === 200 ? (
        <Card className="cardmain">
          <Stack
            direction="row"
            justifyContent="space-between"
            alignItems="center"
            spacing={20}
            className="upperstack"
          >
            <Paper className="paper">
              <Clock />
            </Paper>
            <Paper className="paper">Date</Paper>
          </Stack>
          <Stack>
            <Stack direction="row" justifyContent="space-between">
              <img
                src={`http://openweathermap.org/img/wn/${props.we.weather[0].icon}@2x.png`}
              ></img>

              <Stack>
                <h1>{props.we.name}</h1>
                <p>{props.we.weather[0].description}</p>
              </Stack>
            </Stack>
            <p>feels like {converter(props.we.main.feels_like)}&deg;C</p>
            <h2>{converter(props.we.main.temp)}&deg;C</h2>
            <Stack direction="row" justifyContent="space-between">
              <Paper>L {converter(props.we.main.temp_min)}&deg;F</Paper>
              <Paper>H {converter(props.we.main.temp_max)}&deg;F</Paper>
            </Stack>
          </Stack>
          <Stack direction="row" justifyContent="space-between">
            <Stack direction="row" justifyContent="space-between">
              <Paper></Paper>
              <Paper>{props.we.wind.speed}</Paper>
            </Stack>
            <Stack direction="row" justifyContent="space-between">
              <Paper>image</Paper>
              <Paper>{props.we.main.humidity}</Paper>
            </Stack>
          </Stack>
        </Card>
      ) : null}
    </div>
  );
};
export default Forecast;
