import React, { useEffect, useState } from "react";
import Forecast from "./Forecast";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";

export default function Weather() {
  //using city name
  const [weather, setweather] = useState([]);
  const [city, setcity] = useState([]);

  //using gps (latitude and longitude)
  const [lat, setLat] = useState([]);
  const [long, setLong] = useState([]);

  const uriEncodedCity = encodeURIComponent(city);

  //for using cityname
  const getWeather = (e) => {
    e.preventDefault();
    fetch(
      `https://community-open-weather-map.p.rapidapi.com/weather?q=${uriEncodedCity}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "fd8b6de988msh1b75c7b89409fd2p173556jsnf4d277f4eee3",
        },
      }
    )
      .then((res) => res.json())
      .then((response) => {
        setweather(response);
      })
      .catch((err) => {
        console.error(err);
      });
  };
  //for using gps
  const locationWeather = async (e) => {
    e.preventDefault();
    navigator.geolocation.getCurrentPosition(function (position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });

    await fetch(
      `https://community-open-weather-map.p.rapidapi.com/weather?lat=${lat}&lon=${long}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "fd8b6de988msh1b75c7b89409fd2p173556jsnf4d277f4eee3",
        },
      }
    )
      .then((res) => res.json())
      .then((response) => {
        setweather(response);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <div className="main">
      <Stack
        direction="row"
        justifyContent="space-evenly"
        alignItems="center"
        spacing={0}
      >
        <form className="form" onSubmit={getWeather}>
          <TextField
            label="City"
            variant="standard"
            value={city}
            onChange={(e) => setcity(e.target.value)}
          />

          <Button variant="outlined" onClick={getWeather}>
            Get Weather
          </Button>
          <Button variant="outlined" onClick={locationWeather}>
            GPS
          </Button>
        </form>
      </Stack>
      <Forecast responseObj={weather} />
    </div>
  );
}
