import React, { useEffect, useState } from "react";
import Forecast from "./Forecast";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import "./weather.scss";

export default function Weather() {
  //using city name
  const [weather, setweather] = useState([]);
  const [city, setcity] = useState([]);

  //using gps (latitude and longitude)
  const [lat, setLat] = useState([]);
  const [long, setLong] = useState([]);

  const uriEncodedCity = encodeURIComponent(city);

  //for using cityname
  const getWeather = (e) => {
    e.preventDefault();
    fetch(
      `https://community-open-weather-map.p.rapidapi.com/weather?q=${uriEncodedCity}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "fd8b6de988msh1b75c7b89409fd2p173556jsnf4d277f4eee3",
        },
      }
    )
      .then((res) => res.json())
      .then((response) => {
        setweather(response);
      })
      .catch((err) => {
        console.error(err);
      });
  };
  //for using gps

  //improved location function
  const get = (e) => {
    const success = (position) => {
      e.preventDefault();
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);

      fetch(
        `https://community-open-weather-map.p.rapidapi.com/weather?lat=${lat}&lon=${long}`,
        {
          method: "GET",
          headers: {
            "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
            "x-rapidapi-key":
              "fd8b6de988msh1b75c7b89409fd2p173556jsnf4d277f4eee3",
          },
        }
      )
        .then((res) => res.json())
        .then((response) => {
          setweather(response);
        })
        .catch((err) => {
          console.error(err);
        });
    };

    const error = () => {
      alert("Unable to retrieve location.");
    };

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(success, error);
    } else {
      alert(
        "Your browser does not support location tracking, or permission is denied."
      );
    }
  };

  return (
    <div className="main">
      <Stack
        direction="row"
        justifyContent="center"
        alignItems="center"
        spacing={20}
      >
        <TextField
          label="City"
          variant="standard"
          value={city}
          onChange={(e) => setcity(e.target.value)}
        />

        <Button variant="outlined" onClick={getWeather}>
          Get Weather
        </Button>
        <Button variant="outlined" onClick={get}>
          GPS
        </Button>
      </Stack>
      <Forecast responseObj={weather} />
    </div>
  );
}
