import React, { Component, useState } from "react";
import "./weather.scss";
import Forecast from "./Forecast.jsx";

export default class Weather extends Component {
  render() {
    let [response, setresponse] = useState({});
    function Forecast() {
      fetch(
        "api.openweathermap.org/data/2.5/weather?q=Pune&appid=e6ab6c3c45cc9e2e821ff0301eb9da23"
      ).then((response) => response.json());
    }
    return (
      <div className="main">
        <input type="input" class="form__field" placeholder="City-Name" />

        <button type="submit" className="button" onClick={Forecast}>
          Get Weather
        </button>
        <div>
          <p>{response}</p>
        </div>
      </div>
    );
  }
}
