import React from "react";
import "./forecast.scss";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import { CardHeader } from "@mui/material";
import Stack from "@mui/material/Stack";
import Paper from "@mui/material/Paper";
import { Clock } from "./clock";
import Currdate from "./currdate";

const Forecast = (props) => {
  return (
    <div className="main">
      {props.we.cod === 200 ? (
        <Card className="cardmain">
          <Stack
            direction="row"
            justifyContent="space-between"
            alignItems="center"
            spacing={15}
            className="upperstack"
          >
            <p>
              <Clock />
            </p>
            <p>
              <Currdate />
            </p>
          </Stack>
          <Stack>
            <Stack direction="row" justifyContent="space-between">
              <img
                src={`http://openweathermap.org/img/wn/${props.we.weather[0].icon}@2x.png`}
              ></img>
              <Stack>
                <h1>
                  {props.we.name}, {props.we.sys.country}
                </h1>
                <h5>{props.we.weather[0].description}</h5>
              </Stack>
            </Stack>
            <h4>feels like {props.we.main.feels_like}&deg;C</h4>
            <h2>{props.we.main.temp}&deg;C</h2>
            <Stack direction="row" justifyContent="space-between">
              <h3>Low {props.we.main.temp_min}&deg;C</h3>
              <h3>High {props.we.main.temp_max}&deg;C</h3>
            </Stack>
          </Stack>
          <Stack direction="row" justifyContent="space-between">
            <Stack direction="row" justifyContent="space-between">
              <img src="/wind.png" />
              <Paper>{props.we.wind.speed}m/s</Paper>
            </Stack>
            <Stack direction="row" justifyContent="space-between">
              <img src="/humidity.png" />
              <Paper>{props.we.main.humidity}%</Paper>
            </Stack>
          </Stack>
        </Card>
      ) : null}
    </div>
  );
};
export default Forecast;
