import React, { useEffect, useState } from "react";
import Forecast from "./Forecast";
import "./weather.scss";

export default function Weather() {
  //using city name
  const [weather, setweather] = useState([]);
  const [city, setcity] = useState([]);

  //using gps (latitude and longitude)
  const [lat, setLat] = useState([]);
  const [long, setLong] = useState([]);

  const uriEncodedCity = encodeURIComponent(city);

  //for using cityname
  const getWeather = (e) => {
    e.preventDefault();
    fetch(
      `https://community-open-weather-map.p.rapidapi.com/weather?q=${uriEncodedCity}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "fd8b6de988msh1b75c7b89409fd2p173556jsnf4d277f4eee3",
        },
      }
    )
      .then((res) => res.json())
      .then((response) => {
        setweather(response);
      })
      .catch((err) => {
        console.error(err);
      });
  };
  //for using gps
  const locationWeather = async (e) => {
    await navigator.geolocation.getCurrentPosition(function (position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });

    await fetch(
      `https://community-open-weather-map.p.rapidapi.com/weather?lat=${lat}&lon=${long}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "fd8b6de988msh1b75c7b89409fd2p173556jsnf4d277f4eee3",
        },
      }
    )
      .then((res) => res.json())
      .then((response) => {
        setweather(response);
      })
      .catch((err) => {
        console.error(err);
      });
    e.preventDefault();
  };

  return (
    <div className="main">
      <form className="form" onSubmit={getWeather}>
        <input
          className="input"
          type="text"
          placeholder="City-Name"
          value={city}
          onChange={(e) => setcity(e.target.value)}
        ></input>

        <button className="button">Yes</button>
        <button className="button" onClick={locationWeather}>
          gps
        </button>
      </form>

      <Forecast responseObj={weather} />
    </div>
  );
}
