import React from "react";
import "./forecast.scss";
import Card from "@mui/material/Card";
import Stack from "@mui/material/Stack";
import { Clock } from "./clock";
import Currdate from "./currdate";

const Forecast = (props) => {
  return (
    <div className="main">
      {props.we.cod === 200 ? (
        <Stack className="cardmain">
          <Stack
            direction="row"
            justifyContent="space-between"
            className="upperstack"
            spacing={0}
          >
            <Clock />
            <Currdate />
          </Stack>
          <Stack
            direction="column"
            justifyContent="space-between"
            className="mainstack"
          >
            <Stack direction="row" justifyContent="space-between">
              <img
                src={`http://openweathermap.org/img/wn/${props.we.weather[0].icon}@2x.png`}
              ></img>
              <Stack
                direction="column"
                justifyContent="flex-start"
                alignItems="center"
                spacing={-3}
              >
                <h2>
                  {props.we.name}, {props.we.sys.country}
                </h2>
                <p>{props.we.weather[0].description}</p>
              </Stack>
            </Stack>
            <h4>feels like {props.we.main.feels_like}&deg;C</h4>
            <h1>{props.we.main.temp}&deg;C</h1>
            <Stack direction="row" justifyContent="space-between">
              <h3>Low {props.we.main.temp_min}&deg;C</h3>
              <h3>High {props.we.main.temp_max}&deg;C</h3>
            </Stack>
            <Stack direction="row" justifyContent="space-between">
              <p>{props.we.wind.speed}m/s</p>
              <p>{props.we.main.humidity}%</p>
            </Stack>
          </Stack>
        </Stack>
      ) : null}
    </div>
  );
};
export default Forecast;
