import React, { useState } from "react";

const Forecast = () => {
  let [responseobj, setresponseobj] = useState({});
  function getForecast() {
    fetch(
      "api.openweathermap.org/data/2.5/weather?q=Pune&appid=e6ab6c3c45cc9e2e821ff0301eb9da23"
    )
      .then((res) => res.json())
      .then((result) => {
        setresponseobj(result);
      });
  }

  return (
    <div className="main">
      <input type="input" class="form__field" placeholder="City-Name" />

      <button type="submit" className="button" onClick={getForecast}>
        Get Weather
      </button>
      <div>{responseobj}</div>
    </div>
  );
};
export default Forecast;
