import React from "react";
import "./forecast.scss";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import { CardHeader } from "@mui/material";

const Forecast = (props) => {
  return (
    <div className="main">
      <Card>
        <CardHeader>title={props.responseObj.name}</CardHeader>
      </Card>
      {props.responseObj.cod === 200 ? (
        <div className="forecast">
          <p className="city">{props.responseObj.name}</p>
          <p className="data">
            It is currently {Math.round(props.responseObj.main.temp)} degrees
            out with {props.responseObj.weather[0].description}.
          </p>
        </div>
      ) : null}
    </div>
  );
};
export default Forecast;

useEffect(() => {
  const fetchData = async () => {
    await navigator.geolocation.getCurrentPosition(function (position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });

    await fetch(
      `${process.env.REACT_APP_API_URL}/weather/?lat=${lat}&lon=${long}&units=metric&APPID=${process.env.REACT_APP_API_KEY}`
    )
      .then((res) => res.json())
      .then((result) => {
        setData(result);
        console.log(result);
      });
  };
  fetchData();
}, [lat, long]);
